import React, { Component } from "react";
// import "./assets/css/App.css";
import "../App.css";
import Shelf from "../components/Shelf.js"
import {getAllBooks} from "./Api.js"

export default class Home extends Component {
    componentDidMount(){
        // const books = getAllBooks();
        console.log(books)
      }
  render() {
        const books = getAllBooks();
      const types_of_shelfs = [
        {text:"Currently Reading",value:"currentlyReading"},
        {text:"Want to Read",value:"wantToRead"},
        {text:"Read",value:"read"},
      ]
    return (
      <div>
        <div className="books-list">
          <div className="books-list_title">
            <h1>NReads</h1>
          </div>
          <div className="books-list_content">
            <div>
                {types_of_shelfs.map((types_of_shelf,i) => {
                    return(
                        <Shelf key={i} shelf_name={types_of_shelf.name} shelf_value={types_of_shelf.value} books={books} />
                    )}
                )}
            </div>
          </div>
          <div className="search-btn">
            <button>Add a book</button>
          </div>
        </div>
      </div>
    );
  }
}
