import React, { Component } from 'react';
import BookCard from './BookCard.js'
import {getAllBooks} from "../Api.js"

class Shelf extends Component{
    componentDidMount(){
        const books = getAllBooks();
        console.log(books)
      }
    render(){
        const shelf_choices = [
            {text:"Currently Reading",value:"currentlyReading"},
            {text:"Want to Read",value:"wantToRead"},
            {text:"Read",value:"read"},
            {text:"None",value:"none"},
        ]
        return(
            <div className="shelf">
            <h2 className="shelf-title">{this.props.shelf_name}</h2>
            <div className="shelf-books">
              <ol className="books-box">
                <li>
                  <div className="book">
                    <div className="book-position">
                      <div
                        className="book-cover"
                        style={{
                          width: 128,
                          height: 193,
                          backgroundImage:
                            'url("http://books.google.com/books/content?id=PXa2bby0oQ0C&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api")',
                        }}
                      ></div>
                      <div className="shelf-shfiter">
                        <select>
                          <option value="move" disabled>
                            Move to...
                          </option>
                          {shelf_choices.map((shelf_choice, i) => {
                            return (
                                <option key={i} value={shelf_choice.value}>
                                {shelf_choice.text}
                              </option>
                            ) })}
                        </select>
                      </div>
                    </div>
                    {books.filter((book) => book.shelf == this.props.shelf_value).map((book, i) => {
                    return (
                        <BookCard key={i} book_name={book.title} book_authors={book.authors}/>
                    );
                     })}
                    <div className="book-title">
                      JavaScript: The Good Parts
                    </div>
                    <div className="book-author">Douglas Crockford</div>
                  </div>
                </li>
              </ol>
            </div>
          </div>
        )
    }
}
export default Shelf;
